import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import zxcvbn from 'zxcvbn';
import { useAuth } from '../context/AuthContext';

const RegisterContainer = styled.div`
  max-width: 400px;
  margin: 0 auto;
  padding: 20px;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
`;

const Input = styled.input`
  margin-bottom: 10px;
  padding: 8px;
  font-size: 16px;
`;

const Button = styled.button`
  background-color: var(--primary-color);
  color: white;
  border: none;
  padding: 10px;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    background-color: var(--primary-color-dark);
  }
`;

const ErrorMessage = styled.p`
  color: red;
  margin-top: 10px;
`;

const PasswordStrength = styled.div`
  margin-top: 5px;
  height: 5px;
  background-color: ${props => {
    if (props.score === 0) return 'red';
    if (props.score === 1) return 'orange';
    if (props.score === 2) return 'yellow';
    if (props.score === 3) return 'lightgreen';
    if (props.score === 4) return 'green';
  }};
  width: ${props => (props.score + 1) * 20}%;
`;

const PasswordFeedback = styled.p`
  font-size: 14px;
  margin-top: 5px;
  color: ${props => props.score <= 2 ? 'red' : 'green'};
`;

function Register() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [passwordScore, setPasswordScore] = useState(0);
  const navigate = useNavigate();
  const { register } = useAuth();

  const handlePasswordChange = (e) => {
    const newPassword = e.target.value;
    setPassword(newPassword);
    const result = zxcvbn(newPassword);
    setPasswordScore(result.score);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (passwordScore < 3) {
      setError('Please choose a stronger password');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, email, password }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Registration failed');
      }

      navigate('/login');
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <RegisterContainer>
      <h2>Register for Zecko</h2>
      <Form onSubmit={handleSubmit}>
        <Input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <Input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <Input
          type="password"
          placeholder="Password"
          value={password}
          onChange={handlePasswordChange}
          required
        />
        <PasswordStrength score={passwordScore} />
        <PasswordFeedback score={passwordScore}>
          {passwordScore < 3 ? 'Password is too weak' : 'Password is strong'}
        </PasswordFeedback>
        <Button type="submit">Register</Button>
        {error && <ErrorMessage>{error}</ErrorMessage>}
      </Form>
    </RegisterContainer>
  );
}

export default Register;
